import React from 'react';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            data: {}
        }

    }

    componentDidMount(){
        fetch("https://swapi.co/api/people/1")
            .then(response => response.json())
            .then(data => {
                this.setState({data: data
                })
            })
    }

    render() {
        return (
            <div className="todo-list">
                {this.state.data.name}
            </div>
        )
    }
}


export default App









