import React from 'react';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
        }

    }
    handleChange () {
    }
    render() {
        return (
            <form >
                <input type="text" placeholder="first name" onChange={this.handleChange}/>
            </form>
        )
    }
}


export default App









