import React from 'react';
import todo from './toDoData';
import ToDoItem from './ToDoItem';

class App extends React.Component {
    constructor () {
        super() 
        this.state = {
            todos: todo,
        }
    }

    render() {
        const todoData = this.state.todos.map(item => <ToDoItem key={(item.id)} task={item.task} />)
        return (
            <div class="todo-list">
                {todoData}

            </div>
        )
    }
}

export default App
