import React from 'react';
import todo from './toDoData';
import ToDoItem from './ToDoItem';

class App extends React.Component {
    constructor() {
        super()
        this.state = {

            loading: true,
        }
    }
    componentDidMount(){
        setTimeout(()=>{
            this.setState({loading:false})
        }, 2000)
    }
    render() {
        return (
            <div className="todo-list">
            </div>
        )
    }
}


export default App









