import React from 'react';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            isTrue: true,
        }
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange (event) {
        const { name, value, type, checked } = event.target
        type === "checkbox" ? this.setState({ [name]: checked }) : this.setState({[name]: value})

    }

    render() {
        const st = {
            background: "blue"
        }
        return (
            <form >
                <input type="text" name="firstName" placeholder="First name" onChange={this.handleChange}/>
                <input type="text" name="lastName" placeholder="Last name" onChange={this.handleChange} />
                <input type="checkbox" name="isTrue" checked={this.state.isTrue} onChange={this.handleChange} />
                <input type="radio" name="gender" value="male" checked={this.state.isTrue} onChange={this.handleChange} />
                <input type="radio" name="gender" value="female"checked={this.state.isTrue} onChange={this.handleChange} />

                <h1>{this.state.firstName} {this.state.lastName}</h1>
            </form>

        )
    }
}


export default App









