import React from 'react';
import Conditional from './Conditional';

class App extends React.Component {
    constructor() {
        super()
        this.state = {
            login: true,
        }
    }

    toggleCondition () {
        this.setState({login : this.state.login ? false:true})
    }

    render() {
        return (
            <div className="todo-list">
                <Conditional login={this.state.login} />
            </div>
        )
    }
}


export default App









