import React from 'react';

class App extends React.Component {
    constructor () {
        super() 
        this.state = {
            text: "Helllo!!"
        }
    }
    howerHendler() {
        this.setState({text: "Howered"});
    }
    render() {
        return (
            <div>
                <p>{this.state.text}</p>
                <button onClick={this.howerHendler}></button>

            </div>
        )
    }
}

export default App









