        )
    }
    render() {
        const todoData = this.state.todos.map(item => <ToDoItem 
                                                            key={(item.id)}
                                                            item={item}
                                                            handleChange = {this.handleChange}
                                                            />)
        return (
            <div className="todo-list">
                {todoData}
            </div>
        )
    }
}


export default App









