import React from 'react';

class FormConteiner extends React.Component {
    constructor() {
        super()
        this.state = {
            firstName: "",
            lastName: "",
            age: "",
            gender: "",
            destinaion: "",
            fawColor: "",
            directoryRegistretion: {
                isVegan: false,
                isVarior: false,
                isLuky: false,

            },
        }
        this.handleChange = this.handleChange.bind(this);
    }
    handleChange(event) {
        const { name, value, type, checked } = event.target
        type === "checkbox" ?
            this.setState(prevState => {
                return {
                    directoryRegistretion: {
                        ...prevState.directoryRegistretion,
                        [name]: checked
                    }
                }
            })
            : this.setState({ [name]: value })

    }

    render() {

        return (
            <div>
                <form>
                    <input type="text" name="firstName" placeholder="First name" onChange={this.handleChange} /><hr />
                    <input type="text" name="lastName" placeholder="Last name" onChange={this.handleChange} /><hr />
                    <input type="text" name="age" placeholder="Age" onChange={this.handleChange} /><hr />

                    <label>
                        <input type="radio"
                            name="gender"
                            value="male"
                            checked={this.state.gender === "male"}
                            onChange={this.handleChange} />male
                    </label>
                    <label>
                        <input type="radio"
                            name="gender"
                            value="female"
                            checked={this.state.gender === "female"}
                            onChange={this.handleChange} />Female
                    </label>
                    <select value={this.state.fawColor} onChange={this.handleChange} name="fawColor">
                        <option value="blue">1</option>
                        <option value="red">2</option>
                        <option value="green">3</option>
                    </select>
                    <input type="checkbox" name="isVarior" checked={this.state.directoryRegistretion.isVarior} onChange={this.handleChange} /> You Varior? <hr />
                    <input type="checkbox" name="isVegan" checked={this.state.directoryRegistretion.isVegan} onChange={this.handleChange} /> You Vegan? <hr />
                    <input type="checkbox" name="isLuky" checked={this.state.directoryRegistretion.isLuky} onChange={this.handleChange} /> You Lucky? <hr />
                </form>
                <p>Your name:      {this.state.firstName}</p>
                <p>Your Last Name:     {this.state.lastName}</p>
                <p>Your Age:     {this.state.age}</p>
                <p>Your Gender:     {this.state.gender}</p>
                <p>Varior:     {this.state.directoryRegistretion.isVarior ? "True" : "False"}</p>
                <p>Vegan:     {this.state.directoryRegistretion.isVegan ? "True" : "False"}</p>
                <p>Luky:     {this.state.directoryRegistretion.isLuky ? "True" : "False"}</p>



            </div>
        )
    }
}


export default FormConteiner
